package control;

public class Select2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a = "삼";
		switch(a) {
		case "영" :
			System.out.println(0);
			break;
		case "일" :
			System.out.println(1);
			break;
		case "이" :
			System.out.println(2);
			break;
		case "삼" :
			System.out.println(3);
			break;
		case "사" :
			System.out.println(4);
			break;
		}
	}

}
